from Patterns import *from PatternScanner import CategoryTestnp1 = Pattern(		'np',		[PatternElement(tag=det_tags, quant='?'),		 PatternElement(tag=adj_tags, quant='*'),		 PatternElement(tag=noun_tags)],		CreateConstituentAction('np'))np2 = Pattern(		'np_of_np',		[CategoryTest(['np']),		 PatternElement(stem=['of']),		 CategoryTest(['np'])],		CreateConstituentAction('np'))np3 = Pattern(		'np',		[CategoryTest(['np']),		 PatternElement(stem=['(']),		 PatternElement(tag=['quantity']),		 PatternElement(stem=[')'])		],		CreateConstituentAction('np'))qp1 = Pattern(		'quant',		[PatternElement(tag=['number']),		 PatternElement(stem=['of'], quant='?'),		 PatternElement(tag=['number'], quant='?'),		 PatternElement(tag=['unit'])],		CreateConstituentAction('quantity'))qp2 = Pattern(		'quant',		[PatternElement(tag=['number']),		 PatternElement(stem=['-']),		 PatternElement(tag=['number']),		 PatternElement(tag=['unit'])],		CreateConstituentAction('quantity'))NOUN_PHRASE_PATTERNS = [np1, np2, np3, qp1, qp2]